import styled from "styled-components";

const Button = styled.div`
  border: 2px solid gray;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  font-size: 16px;
  margin: 2px;
  border-radius: 5px;
  &:hover {
    background-color: #cccccc;
  }
`;

export default Button;
